mapper.select:
select NVL(count(*),0) from board
where(
	worker like '%${find}%' or subject like '%${find}%' 
	or content  like '%${find}%'
)
and part=#{part}

     	select * from (
		  select rownum no, a.* from( 
			select * from board 
			  where (worker like '%${find}%' or subject like '%${find}%' or  
			  		 content  like'%${find}%' ) 
			  and part=#{part} 
			  order by grp desc, deep asc )a
			  ) where no between ${startNo} and ${endNo}
